import React, { Component } from 'react';

class cart extends Component {
    state = {
    }

    render() {
        return <div className="shopping-cart">
            <h2>Warenkorb</h2>
            {this.props.items.map(items => <div key={items.name}>{items.amount}x {items.name} {items.price}€</div>)}
            <h4>Gesamtpreis: {this.props.totalPrice.toFixed(2)}€</h4>




        </div>;
    }
}

export default cart
