import React, { useState } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Product from './product';

function Theke() {
  // State für den Warenkorb
  const [cart, setCart] = useState([]);

  // Funktion zum Hinzufügen eines Produkts zum Warenkorb
  const addToCart = (title, preis) => {
    const updatedCart = [...cart, { title, preis }];
    setCart(updatedCart);
  };

  return (
    <div>
      <h2>Inhalt für Tab 2 hier</h2>
      <Row>
        <Col>
          <Product
            title="Produkt 1"
            img="/images/pommes.jpg"
            preis={2.50}
            onAdd={() => addToCart("Produkt 1", 2.50)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 2"
            img="/images/schnitzel.jpg"
            preis={3.00}
            onAdd={() => addToCart("Produkt 2", 3.00)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 3"
            img="/images/wecken.jpg"
            preis={1.50}
            onAdd={() => addToCart("Produkt 3", 1.50)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 4"
            img="/images/wurst.jpg"
            preis={2.50}
            onAdd={() => addToCart("Produkt 4", 2.50)} // Klick-Handler für den Button
          />
        </Col>
      </Row>

      {/* Anzeige des Warenkorbs */}
      <div>
        <h3>Warenkorb</h3>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.title} - {item.preis.toFixed(2)} €
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Theke;
