import React, { useState } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button'; // Importieren Sie die Button-Komponente von react-bootstrap
import Product from './product'; // Änderung des Imports

function Theke() {
  // State für den Warenkorb und Gesamtpreis
  const [cart, setCart] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  // Funktion zum Hinzufügen eines Produkts zum Warenkorb
  const addToCart = (title, preis) => {
    // Prüfen, ob das Produkt bereits im Warenkorb ist
    const productIndex = cart.findIndex(item => item.title === title);

    if (productIndex !== -1) {
      // Wenn das Produkt bereits im Warenkorb ist, kopiere den aktuellen Warenkorb
      const updatedCart = [...cart];

      // Erhöhe die Anzahl des vorhandenen Produkts
      updatedCart[productIndex].anzahl += 1;

      // Erhöhe den Gesamtpreis um den Produktpreis
      setTotalPrice(totalPrice + preis);

      // Setze den aktualisierten Warenkorb
      setCart(updatedCart);
    } else {
      // Wenn das Produkt nicht im Warenkorb ist, füge es hinzu
      const updatedCart = [...cart, { title, preis, anzahl: 1 }];

      // Erhöhe den Gesamtpreis um den Produktpreis
      setTotalPrice(totalPrice + preis);

      // Setze den aktualisierten Warenkorb
      setCart(updatedCart);
    }
  };

  // Funktion zum Abschicken der Bestellung
  const sendOrder = () => {
    // Hier können Sie den Code zum Abschicken der Bestellung an den Server einfügen
    // Zum Beispiel eine fetch-Anfrage an Ihren Server
    console.log("Bestellung abgeschickt:", cart);
    // Hier können Sie den Warenkorb leeren und den Gesamtpreis zurücksetzen
    setCart([]);
    setTotalPrice(0);
  };

  return (
    <div>
      <h2>Inhalt für Tab 2 hier</h2>
      <Row>
        <Col>
          <Product
            title="Produkt 1"
            img="/images/pommes.jpg"
            preis={2.50}
            onAdd={() => addToCart("Produkt 1", 2.50)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 2"
            img="/images/schnitzel.jpg"
            preis={3.00}
            onAdd={() => addToCart("Produkt 2", 3.00)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 3"
            img="/images/wecken.jpg"
            preis={1.50}
            onAdd={() => addToCart("Produkt 3", 1.50)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 4"
            img="/images/wurst.jpg"
            preis={2.50}
            onAdd={() => addToCart("Produkt 4", 2.50)} // Klick-Handler für den Button
          />
        </Col>
      </Row>

      {/* Anzeige des Warenkorbs und Gesamtpreises */}
      <div>
        <h3>Warenkorb</h3>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.anzahl}x {item.title} - {item.preis.toFixed(2)} €
            </li>
          ))}
        </ul>
        <h4>Gesamtpreis: {totalPrice.toFixed(2)} €</h4>
      </div>

      {/* Button zum Abschicken der Bestellung */}
      <Button variant="primary" onClick={sendOrder}>Bestellung abschicken</Button>
    </div>
  );
}

export default Theke;
