import React from 'react';
import Cart from "./cart";
import Kitchen from './kitchen_logic';
import Product from "./components/product";

function Küche() {
    return <React.Fragment>
    <div className="main-container">
        <div className='container'>
            <Product onAdd={() => this.addItem(1, "Pommes", 2.50)} title="Pommes" img="/images/pommes.jpg" description="2,50€" />
            <Product onAdd={() => this.addItem(1, "Schnitzel", 3.0)} title="Schnitzel" img="/images/schnitzel.jpg" description="3,00€" />
            <Product onAdd={() => this.addItem(1, "Wecken", 1.50)} title="Wecken" img="/images/wecken.jpg" description="1,50€" />
            <Product onAdd={() => this.addItem(1, "Wurst", 2.50)} title="Wurst" img="/images/wurst.jpg" description="2,50€" />
        </div>
        <Cart items={this.state.items} totalPrice={this.state.totalPrice}/>
        <button onClick={this.sendOrderToServer}>Bestellung absenden</button>
    </div>
    <Kitchen />
</React.Fragment>;
}

export default Küche;
