import React, { useState } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Product from './product';
import Cart from './Cart'; // Hier wird die Cart-Komponente importiert

function Theke() {
  // Zustand für den Warenkorb
  const [cartItems, setCartItems] = useState([]);

  // Funktion zum Hinzufügen eines Produkts zum Warenkorb
  const addToCart = (title, preis) => {
    const newItem = { title, preis };
    setCartItems([...cartItems, newItem]);
  };

  return (
    <div>
      <h2>Inhalt für Tab 2 hier</h2>
      <Row>
        <Col>
          <Product
            title="Produkt 1"
            imgSrc="holder.js/100px180"
            preis={2.50}
            onAdd={() => addToCart("Produkt 1", 2.50)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 2"
            imgSrc="holder.js/100px180"
            preis={3.00}
            onAdd={() => addToCart("Produkt 2", 3.00)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 3"
            imgSrc="holder.js/100px180"
            preis={1.50}
            onAdd={() => addToCart("Produkt 3", 1.50)} // Klick-Handler für den Button
          />
        </Col>
        <Col>
          <Product
            title="Produkt 4"
            imgSrc="holder.js/100px180"
            preis={2.50}
            onAdd={() => addToCart("Produkt 4", 2.50)} // Klick-Handler für den Button
          />
        </Col>
      </Row>
      <Cart cartItems={cartItems} /> {/* Die Cart-Komponente wird mit dem Warenkorbinhalt angezeigt */}
    </div>
  );
}

export default Theke;
