import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

function Product(props) {
  const { title, imgSrc, preis, onAdd } = props;
  const formattedPreis = preis.toFixed(2);

  // Funktion zum Hinzufügen des Produkts zum Warenkorb
  const addToCart = () => {
    onAdd(title, preis);
  };

  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={imgSrc} />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>
          Preis: {formattedPreis} €
        </Card.Text>
        <Button variant="primary" onClick={addToCart}>Hinzufügen</Button>
      </Card.Body>
    </Card>
  );
}

export default Product;
