import React, { useState } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Product from './product';

function Theke() {
  const [cart, setCart] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const addToCart = (title, preis) => {
    const productIndex = cart.findIndex(item => item.title === title);

    if (productIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productIndex].anzahl += 1;
      setTotalPrice(totalPrice + preis);
      setCart(updatedCart);
    } else {
      const updatedCart = [...cart, { title, preis, anzahl: 1 }];
      setTotalPrice(totalPrice + preis);
      setCart(updatedCart);
    }
  };

  const sendOrder = () => {
    console.log("Bestellung abgeschickt:", cart);
    setCart([]);
    setTotalPrice(0);
  };

  return (
    <div>
      <h2>Inhalt für Tab 2 hier</h2>
      <Row>
        <Col>
          <Product
            title="Produkt 1"
            img="/images/pommes.jpg"
            preis={2.50}
            onAdd={() => addToCart("Produkt 1", 2.50)}
          />
        </Col>
        <Col>
          <Product
            title="Produkt 2"
            img="/images/schnitzel.jpg"
            preis={3.00}
            onAdd={() => addToCart("Produkt 2", 3.00)}
          />
        </Col>
        <Col>
          <Product
            title="Produkt 3"
            img="/images/wecken.jpg"
            preis={1.50}
            onAdd={() => addToCart("Produkt 3", 1.50)}
          />
        </Col>
        <Col>
          <Product
            title="Produkt 4"
            img="/images/wurst.jpg"
            preis={2.50}
            onAdd={() => addToCart("Produkt 4", 2.50)}
          />
        </Col>
      </Row>

      <div className="my-4"> {/* Abstand zwischen Produkten und Warenkorb */}
        <div className="border p-3"> {/* Rahmen um den Warenkorb */}
          <h3>Warenkorb</h3>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>
                {item.anzahl}x {item.title} - {item.preis.toFixed(2)} €
              </li>
            ))}
          </ul>
          <h4>Gesamtpreis: {totalPrice.toFixed(2)} €</h4>
        </div>
      </div>

      <Button variant="primary" onClick={sendOrder}>Bestellung abschicken</Button>
    </div>
  );
}

export default Theke;
